import React from 'react';
import AudioButton from './AudioButton';
import Options from './Options';

class Phonics extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      optionsList: ['Uno', 'Dos', 'Tres']
    };

    // This binding is necessary to make `this` work in the callback
    this.updateOptionsList = this.updateOptionsList.bind(this);
  }

  updateOptionsList() {
    // this.setState(state => ({
    //   isToggleOn: !state.isToggleOn
    // }));
    console.log('FUNCIONA CLICK');
  }
  
  render() {
    return <div>
        <h1>The Phonics game</h1>
        <AudioButton/>
        <Options optionsList={this.state.optionsList}/>
        <div>
          <button onClick={this.updateOptionsList}>Otro</button>
        </div>
    </div>;
  }
}

export default Phonics;
