import React from 'react';
import AudioButton from './AudioButton';
import Options from './Options';

class Phonics extends React.Component {

  constructor(props) {
    super(props);
    this.optionsLists = [
      {
        sound: 'blablabla',
        optionsList: ['Uno', 'Dos', 'Tres'],
      },
      {
        sound: 'bliblibli',
        optionsList: ['Cuatro', 'Cinco', 'Seis'],
      },
      {
        sound: 'blobloblo',
        optionsList: ['Siete', 'Ocho', 'Nueve'],
      }
    ];
    this.state = {
      currentOptionList: 0
    };

    // This binding is necessary to make `this` work in the callback
    this.updateOptionsList = this.updateOptionsList.bind(this);
  }

  updateOptionsList() {
    this.setState((state) => ({
      currentOptionList: state.currentOptionList + 1
    }));
    console.log('FUNCIONA CLICK');
  }
  
  render() {
    return <div>
        <h1>The Phonics game</h1>
        <AudioButton/>
        <Options optionsList={this.optionsLists[this.state.currentOptionList]}/>
        <div>
          <button onClick={this.updateOptionsList}>Otro</button>
        </div>
    </div>;
  }
}

export default Phonics;
